import React, { Component } from 'react'
import Bunny from './Bunny'
import Grass from './Grass'
import Bomb  from './Bomb'
import { EMPTY, OCCUPIED, EXPLODED } from '../shared/constants'

class Main extends Component {
  constructor(props) {
    super(props)

    this.state = {
      grid: [
        [EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY],
        [OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED],
        [EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED],
        [EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY],
        [OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED],
        [EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED],
        [EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY],
        [OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED],
        [EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED],
        [EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY]
      ]
    }
  }

  tile = (status) => {
    switch(status) {
      case EMPTY:
        return (<Grass />)
      case OCCUPIED:
        return (<Bunny />)
      case EXPLODED:
        return (<Bomb />)
      default:
        return null;
    }
  }

  rowTiles = (tiles) => {
    return tiles.map((status) => tile(status))
  }

  grid = (rows) => {
    return rows.map((row) => rowTiles(row)).join(<br />)
  }

  render() {
    return (
      <div className="container">
        <div className="col-12">
          <grid rows={this.state.grid} />
        </div>
      </div>
    )
  }
}

export default Main
