import React, { Component } from 'react'
import { OCCUPIED } from '../shared/constants'

class Bomb extends Component {
  constructor(props) {
    super(props)
  }

  render() {
    return (
      <img src="/explode.gif" className="tile" onClick={() => {this.props.onClick(this.props.row, this.props.col, OCCUPIED)}} />
    )
  }
}

export default Bomb
