import React, { Component } from 'react'
import { OCCUPIED } from '../shared/constants'

class Bomb extends Component {
  constructor(props) {
    super(props)

    this.state = {
      iterate: 0
    }
  }

  source = (url) => {
    this.setState({iterate: this.state.iterate + 1})

    return `${url}?x=${iterate}`
  }

  render() {
    return (
      <img src={this.source('/explode.gif')} className="tile" onClick={() => {this.props.onClick(this.props.row, this.props.col, OCCUPIED)}} />
    )
  }
}

export default Bomb
