import React, { Component } from 'react'
import Bunny from './Bunny'
import Grass from './Grass'
import Bomb  from './Bomb'
import { EMPTY, OCCUPIED, EXPLODED } from '../shared/constants'
import { Button } from 'reactstrap'

class Main extends Component {
  constructor(props) {
    super(props)

    this.state = {
      grid: [
        [EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY],
        [OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED],
        [EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED],
        [EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY],
        [OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED],
        [EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED],
        [EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY],
        [OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED],
        [EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED],
        [EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY, OCCUPIED, EXPLODED, EMPTY]
      ]
    }
  }

  tile = (status) => {
    switch(status) {
      case EMPTY:
        return (<Grass />)
      case OCCUPIED:
        return (<Bunny />)
      case EXPLODED:
        return (<Bomb />)
      default:
        return null;
    }
  }

  rowTiles = (tiles) => {
    return tiles.map((status) => this.tile(status))
  }

  gridTiles = (rows) => {
    return (
      <div>
        { rows.map((row) => this.rowTiles(row)) }
      </div>
    )
  }

  render() {
    return (
      <div className="container">
        <div className="col-12">
          { this.gridTiles(this.state.grid) }

          <Button color="primary">Test</Button>
        </div>
      </div>
    )
  }
}

export default Main
