import React, { Component } from 'react'
import { OCCUPIED } from '../shared/constants'

class Bomb extends Component {
  constructor(props) {
    super(props)

    this.state = {
      loaded: ''
    }
  }

  loadSource = (url) => {
    this.setState({loaded: ''})

    setTimeout(() => {
      this.setState({loaded: url})
    }, 0)
  }

  render() {
    this.loadSource('/explode.gif')

    return (
      <img src={ this.state.loaded } className="tile" onClick={() => {this.props.onClick(this.props.row, this.props.col, OCCUPIED)}} />
    )
  }
}

export default Bomb
