import React, { Component } from 'react'
import { OCCUPIED } from '../shared/constants'

class Bomb extends Component {
  constructor(props) {
    super(props)

    this.iterate = 0
    this.original = true
  }

  componentDidMount() {
    this.iterate++;
  }

  // componentDidUpdate() {
  //   this.iterate++;
  // }

  source = (url) => {
    return `${url}?x=${this.iterate}`
  }

  render() {
    return (
      <img src={this.source('/explode.gif')} className="tile" onClick={() => {this.props.onClick(this.props.row, this.props.col, OCCUPIED)}} />
    )
  }
}

export default Bomb
